        private string opGetNumberOnly(string str)
        {
            string str1 = "";
            str = str.ToLower().Replace("(r )", ",").Replace("(m )", ",").Replace("(r)", "").Replace("(m)", "").Replace("(R)", ",").Replace("(M)", ",").Replace("Â®", ",").Replace("(mobile)", ",").Replace("(office)", ",").Replace("(ll)", ",").Replace("-", "").Replace(".", "").Replace("(res)", ",").Trim();

