        private string opReplaceGrade(string GradeValue)
        {
            try
            {
                return GradeValue.ToLower().Replace(":", "").Replace("=", "").Replace("-", "").Replace("cgpa", "").Replace("gpa â€“", "").Replace("gpa", "").Replace("c.g.p.a", "").Replace("g.p.a", "").Replace("c g p a", "").Replace("g p a", "").Replace("percentage", "").Replace("(", "").Replace(")", "");
            }
            catch
            {
                return "";
            }
        }

